table.sql:
			create table T_DEMO
			(
			ID VARCHAR2(32) not null,
			NAME VARCHAR2(200),
			AGE NUMBER(3),
			REMARK VARCHAR2(2000),
			constraint PK_T_DEMO primary key (ID)
			);

			comment on table T_DEMO is
			'示例表';

			comment on column T_DEMO.ID is
			'主键';

			comment on column T_DEMO.NAME is
			'姓名';

			comment on column T_DEMO.AGE is
			'年龄';

			comment on column T_DEMO.REMARK is
			'备注';
